import { Chat, Action, Message, HeroCard } from './main';
import { BaseEvent } from '@progress/kendo-react-common';
/**
 * The arguments for the `actionexecute` event of the hero card.
 */
export interface HeroCardActionExecuteEvent extends BaseEvent<HeroCard> {
    /**
     * The executed action.
     */
    action: Action;
}
/**
 * The arguments for the `actionexecute` event of the Chat.
 */
export interface ChatActionExecuteEvent extends BaseEvent<Chat> {
    /**
     * The executed action.
     */
    action: Action;
}
/**
 * The arguments for the `messagesend` event of the Chat.
 */
export interface ChatMessageSendEvent extends BaseEvent<Chat> {
    /**
     * The new message.
     */
    message: Message;
}
/**
 * The arguments for the `toolbaractionexecute` event of the Chat.
 */
export interface ChatToolbarActionExecuteEvent extends BaseEvent<Chat> {
    /**
     * The new toolbarShow value.
     */
    show: boolean;
}
